global.set('wordsCount', (notification) => {
  const pattern = '\\w+';
  const reg = new RegExp(pattern, 'g');
  return (notification.match(reg) || []).length;
}, 'memoryOnly');

global.set('getReadingTime', (notification) => {
  const WORDS_PER_MIN = 275;
  const wordCount = global.get('wordsCount', 'memoryOnly')(notification) //wordsCount(notification);
  const wordTime = wordCount / WORDS_PER_MIN;
  const wordTimeMilliSec =  wordTime * 60 * 1000;
  return Math.ceil(wordTimeMilliSec);
}, 'memoryOnly');
